// Funcion para cambiar cualquier casilla individualmente
void cambiarEstadoDeCasilla(int posicionX, int posicionY, unsigned char charSuperiorIzquierdo, unsigned char charSuperiorDerecho, unsigned char charInferiorIzquierdo, unsigned char charInferiorDerecho, unsigned char pasable, unsigned char color) {
    // Asignamos a la casilla en la posicion elegida los atributos que se le quieren poner
    pantalla[posicionX][posicionY][0] = charSuperiorIzquierdo;
    pantalla[posicionX][posicionY][1] = charSuperiorDerecho;
    pantalla[posicionX][posicionY][2] = charInferiorIzquierdo;
    pantalla[posicionX][posicionY][3] = charInferiorDerecho;
    pantalla[posicionX][posicionY][4] = pasable;
    pantalla[posicionX][posicionY][5] = color;
}

// int lineaDeConsola determina en donde se pondra el mensaje
// string mensaje determina el mensaje que se va a desplegar (maximo 116 caracteres)
// bool limpiar determina si se quitara el mensaje actual de la consola
void imprimirMensaje(int lineaDeConsola, string mensaje, bool limpiar)
{
    if (limpiar)
        // Para limpiar la linea de la consola imprimimos un gran mensaje vacio
    {
        mensaje = "                                                                                                                    ";
    }

    // Obtenemos la longitud del mensaje para saber cuantos caracteres imprimir
    int longitudMensaje = mensaje.length();

    if (longitudMensaje % 2 != 0) {
        // Cuando un mensaje termina en una cantidad no par de letras, la ultima letra no se imprime, por lo que aqui arreglamos eso
        longitudMensaje++;
        mensaje += " ";
    }


    // Si el numero de linea es par, queremos imprimir en la parte superior de la casilla
    if (lineaDeConsola % 2 == 0)
    {
        // Parte superior de casilla
        for (int i = 0; i < longitudMensaje / 2; i++)
        {
            pantalla[15 + lineaDeConsola / 2][i + 1][0] = mensaje[i * 2];
            pantalla[15 + lineaDeConsola / 2][i + 1][1] = mensaje[(i * 2) + 1];
        }

    }
    else
    {
        for (int i = 0; i < longitudMensaje / 2; i++)
            // Parte inferior de casilla
        {
            pantalla[15 + lineaDeConsola / 2][i + 1][2] = mensaje[i * 2];
            pantalla[15 + lineaDeConsola / 2][i + 1][3] = mensaje[(i * 2) + 1];

        }
    }
}

// Funcion que revisa si hay alguna casilla cercana interactuable y lo imprime a la consola
void revisarCasillasCercanas()
{
    // Si el jugador esta al lado de una casilla con el color 'e' (color asignado a los arboles), entonces...
    if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'e' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'e' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'e' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'e')
    {
        if (interactuandoConArbol) {
            // Terminamos la interaccion con el arbol
            interactuandoConArbol = false;
            // Si el jugador no esta interactuando con el arbol, entonces presenta la opcion de interactuar
        }
        else {
            imprimirMensaje(1, " ", true);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(1, "Algo esta moviendose en el arbol...", false);
            imprimirMensaje(2, "Presiona \"E\" para investigar", false);
        }

        // Funcionamiento igual al del arbol, revisa si una casilla cercana es del color asignado a los libreros
    }
    else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'h' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'h' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'h' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'h') {
        if (interactuandoConLibreria) {
            interactuandoConLibreria = false;
        }
        else {
            imprimirMensaje(1, " ", true);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(1, "Puedes leer un libro...", false);
            imprimirMensaje(2, "Presiona \"E\" para investigar", false);
        }

        // Funcionamiento igual al arbol, revisa si una casilla cercana es del color asignado a la casa de Alexito
    }
    else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'k' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'k' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'k' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'k') {
        if (interactuandoConCasaAlexito) {
            // Terminamos la interaccion con la casa de Alexito
            interactuandoConCasaAlexito = false;
        }
        else if (alexitoDesbloqueado) {
            imprimirMensaje(1, " ", true);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(1, "Puedes avanzar al siguiente acto.", false);
            imprimirMensaje(2, "Llega al final del camino...", false);
        }
        else {
            imprimirMensaje(1, " ", true);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(1, "Parece ser una casa, vez a una silhueta adentro...", false);
            imprimirMensaje(2, "Presiona \"E\" para investigar", false);
        }

        // Si no hay ninguna casilla cercana relevante, entonces solo imprime instrucciones basicas
    }
    else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'z' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'z' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'z' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'z')
    {
        if (interactuandoConPortal) {
            // Terminamos la interaccion con el portal
            interactuandoConPortal = false;
            // Si el jugador no esta interactuando con el portal, entonces presenta la opcion de interactuar
        }
        else {
            imprimirMensaje(1, " ", true);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(1, "Ves una libreria a lo lejos", false);
            imprimirMensaje(2, "Presiona \"E\" para viajar", false);
        }
        cercaDePortal = true;
    }
    else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'z' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'z' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'z' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'z')
    {
        if (interactuandoConPortal) {
            // Terminamos la interaccion con el portal
            interactuandoConPortal = false;
            // Si el jugador no esta interactuando con el portal, entonces presenta la opcion de interactuar
        }
        else {
            imprimirMensaje(1, " ", true);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(1, "Ves una libreria a lo lejos", false);
            imprimirMensaje(2, "Presiona \"E\" para viajar", false);
        }
        cercaDePortal = true;
    }
    // Si no hay ninguna casilla cercana relevante, entonces solo imprime instrucciones basicas
    else {
        imprimirMensaje(1, " ", true);
        imprimirMensaje(2, " ", true);
        imprimirMensaje(1, conseguirMensaje(15), false);
        cercaDeArbol = false;
        cercaDeLibreria = false;
        cercaDeCasaAlexito = false;
        cercaDeTecladoNumerico = false;
    }
};

