// Funcion que desplegara la matriz completa (El pantalla)
void imprimirPantalla() {
    // Ciclo que se encarga de imprimir cada elemento de la matriz

    // Se itera en base a las filas de la matriz
    for (int i = 0; i < 20; i++) {
        // Se itera en base a las columnas de la matriz
        for (int j = 0; j < 60; j++) {
            // Se manda a llamar la funcion del color, cambiando el color a el establecido en el elemento con indice 5 de la casilla actual
            // Se despliegan las dos primeros elementos de la casilla
            cout << colores(pantalla[i][j][5]) << colorFondo(pantalla[i][j][5]) << pantalla[i][j][0] << pantalla[i][j][1] << "\x1b[1;0m";
            //cout << "\x1b[38;2;153;106;78m" << pantalla[i][j][0] << pantalla[i][j][1] << "\x1b[1;39m";
            // Casilla:
            // [][] <- Fila que se imprime
            // [][]
        }
        // Salto de linea para pasar a los siguientes dos elementos de la casilla
        cout << endl;

        // Se repite el proceso con la fila de abajo
        for (int j = 0; j < 60; j++) {
            // Se manda a llamar la funcion del color, cambiando el color a el establecido en el elemento con indice 5 de la casilla actual
            //colores(pantalla[i][j][5]);
            // Se imprimen los primeros dos elementos de la casilla
            cout << colores(pantalla[i][j][5]) << colorFondo(pantalla[i][j][5]) << pantalla[i][j][2] << pantalla[i][j][3] << "\x1b[1;0m";
            // Casilla:
            // [][]
            // [][] <- Fila que se imprime
        }
        cout << endl;
    }
}

