// Procedimiento donde se captura lo ingresado por el usuario
void entradaUsuario() {
    char caracterIngresado = _getch();
    // Se manda a llamar la funcion para guardar la casilla
    actualizarEstadoPrevioDeCasilla();
    modeloDeJugador[0] = '@';
    modeloDeJugador[1] = '>';
    modeloDeJugador[2] = '^';
    modeloDeJugador[3] = ' ';
    modeloDeJugador[4] = 'p';
    cambioPantalla(actoActual);
    // Switch que decide lo que se hara en base a inputs de movimiento (w, a, s, d), u otros
    switch (caracterIngresado) {
        // El jugador decide moverse hacia arriba
    case 'w':
        if (pantalla[posicionJugadorX - 1][posicionJugadorY][4] == '1') {
            // Se mueve de posicion al personaje
            posicionJugadorX--;
        }
        break;
        // El jugador decide moverse a la izquierda
    case 'a':
        if (pantalla[posicionJugadorX][posicionJugadorY - 1][4] == '1') {
            posicionJugadorY--;
        }
        break;
        // El jugador decide moverse hacia abajo
    case 's':
        if (pantalla[posicionJugadorX + 1][posicionJugadorY][4] == '1') {
            posicionJugadorX++;
        }
        break;
        // El jugador decide moverse a la derecha
    case 'd':
        if (pantalla[posicionJugadorX][posicionJugadorY + 1][4] == '1') {
            posicionJugadorY++;
        }
        break;
        // Tecla debugging de imprimirMensaje "easter egg"
    case 'i':
        imprimirMensaje(1, " ", true);
        imprimirMensaje(2, " ", true);
        imprimirMensaje(3, " ", true);
        imprimirMensaje(4, " ", true);
        break;
        // Tecla debugging de imprimirMensaje "easter egg"
    case 'l':
        imprimirMensaje(2, "Bienvenido a Farlan", false);
        imprimirMensaje(4, "Mensaje test", false);
        imprimirMensaje(1, "Erace una vez", false);
        imprimirMensaje(3, "Diego kano kano kano kano kano kano kano kano kano kano kano kano", false);
        break;
        // El jugador decide interactuar
    case 'e':
        // Si el jugador interactua, y la casilla cercana es un arbol, entonces haz lo siguiente...
        if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'e' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'e' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'e' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'e')
        {
            interactuandoConArbol = true;
            // Conseguimos un numero al azar del 0 al 9
            int numeroRandom = (rand() % 10);

            // Con una probablidad de uno en diez, damos una moneda
            if (numeroRandom == 1)
            {
                // Si aun no tiene las tres monedas, solo presentar moneda
                if (monedas < 3) {
                    imprimirMensaje(1, " ", true);
                    imprimirMensaje(1, "Encontraste una moneda!!!", false);
                    monedas = monedas + 1;
                }
                // Si ya tiene tres monedas y encuentra una cuarta, entonces presentar que ya es posible pedir la ayuda de Alexito
                else
                {
                    cambioPantalla(3);
                    modeloDeJugador[0] = pantalla[posicionJugadorX][posicionJugadorY][0];
                    modeloDeJugador[1] = pantalla[posicionJugadorX][posicionJugadorY][1];
                    modeloDeJugador[2] = pantalla[posicionJugadorX][posicionJugadorY][2];
                    modeloDeJugador[3] = pantalla[posicionJugadorX][posicionJugadorY][3];
                    modeloDeJugador[4] = pantalla[posicionJugadorX][posicionJugadorY][4];

                    imprimirMensaje(1, " ", true);
                    imprimirMensaje(2, " ", true);
                    imprimirMensaje(3, " ", true);
                    imprimirMensaje(5, " ", true);
                    imprimirMensaje(1, "APARECIO UN FANTASMA!", false);
                    imprimirMensaje(2, "Matate....no la salves...", false);
                    imprimirMensaje(3, "Desaparece el fantasma...", false);
                    imprimirMensaje(5, "Ahora puedes acceder a la tienda de Alexito.", false);
                    monedas = monedas + 1;
                    
                }
            }
            else
            {
                // Almacenamos los diez posibles mensajes para un muerto
                string mensajes[10] = {
                    "Aparecio un muerto... pero no encontraste nada util en el arbol",
                    "Este es un mensaje imposible de obtener.",
                    "Hay un muerto dentro del tronco... pero no encontraste nada util.",
                    "Diego kano es un imbecil!.. pero no encontraste nada util.",
                    "Salio corriendo una ardilla!!! pero no encontraste nada util.",
                    "Hay una serpiente en una bota... pero no encontraste nada util.",
                    "Coronel te dice \"hola :3\", pero no encontraste nada util.",
                    "Mueves unas ramas, pero no encontraste nada util.",
                    "Sacudes el arbol, pero no encontraste nada util.",
                    "Diegogo sale corriendo detras del arbol!!, pero no encontraste nada util.",
                };

                // Imprimimos el mensaje al azar
                imprimirMensaje(1, " ", true);
                imprimirMensaje(2, " ", true);
                imprimirMensaje(1, mensajes[numeroRandom], false);
                imprimirMensaje(2, "Presiona \"E\" para seguir investigando", false);
            }
            // Checamos si una casilla cerca del jugador del color de un librero, entonces...
        }
        else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'h' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'h' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'h' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'h') {
            // Elegimos uno de doce posibles libros a imprimir
            int numeroRandom = (rand() % 12) * 5;
            imprimirMensaje(1, " ", true);
            imprimirMensaje(1, conseguirPasajeDeLibro(numeroRandom), false);
            imprimirMensaje(2, " ", true);
            imprimirMensaje(2, conseguirPasajeDeLibro(numeroRandom + 1), false);
            imprimirMensaje(3, " ", true);
            imprimirMensaje(3, conseguirPasajeDeLibro(numeroRandom + 2), false);
            imprimirMensaje(4, " ", true);
            imprimirMensaje(4, conseguirPasajeDeLibro(numeroRandom + 3), false);
            imprimirMensaje(5, " ", true);
            imprimirMensaje(5, conseguirPasajeDeLibro(numeroRandom + 4), false);

            // Si una de las casillas cercanas al jugador es del color de la casa de Alexito, entonces...
        }
        else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'k' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'k' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'k' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'k') {
            // Senalamos que el jugador esta interactuando con la casa de Alexito para que el mensaje de interactuar no aparezca
            interactuandoConCasaAlexito = true;
            // Si ya interactuamos con alexito una vez que le dimos las monedas, entonces...
            if (alexitoDesbloqueado) {
                cambioPantalla(4);
                modeloDeJugador[0] = pantalla[posicionJugadorX][posicionJugadorY][0];
                modeloDeJugador[1] = pantalla[posicionJugadorX][posicionJugadorY][1];
                modeloDeJugador[2] = pantalla[posicionJugadorX][posicionJugadorY][2];
                modeloDeJugador[3] = pantalla[posicionJugadorX][posicionJugadorY][3];
                modeloDeJugador[4] = pantalla[posicionJugadorX][posicionJugadorY][4];

                imprimirMensaje(1, " ", true);
                imprimirMensaje(1, "Que esperas!?", false);
                imprimirMensaje(2, " ", false);
                imprimirMensaje(2, "Estoy listo para irnos a Lodric", false);
                imprimirMensaje(3, " ", true);
                imprimirMensaje(3, "Continua el camino.", false);
                // Si el jugador ya tiene mas de tres monedas, desbloquear a Alexito
            }
            else if (monedas > 3) {
                cambioPantalla(4);
                modeloDeJugador[0] = pantalla[posicionJugadorX][posicionJugadorY][0];
                modeloDeJugador[1] = pantalla[posicionJugadorX][posicionJugadorY][1];
                modeloDeJugador[2] = pantalla[posicionJugadorX][posicionJugadorY][2];
                modeloDeJugador[3] = pantalla[posicionJugadorX][posicionJugadorY][3];
                modeloDeJugador[4] = pantalla[posicionJugadorX][posicionJugadorY][4];

                imprimirMensaje(1, " ", true);
                imprimirMensaje(1, "Las conseguiste!", false);
                imprimirMensaje(2, " ", true);
                imprimirMensaje(2, "Damelas gracias gracias, ahorita empaco mis cosas y nos vamos", false);
                alexitoDesbloqueado = true;
                cambiarEstadoDeCasilla(9, 59, '%', '%', '%', '%', 0, 'z');
                monedas = 0;
                imprimirHistoria(1);
                // Si aun no tiene mas de tres monedas
            }
            else {
                cambioPantalla(4);
                modeloDeJugador[0] = pantalla[posicionJugadorX][posicionJugadorY][0];
                modeloDeJugador[1] = pantalla[posicionJugadorX][posicionJugadorY][1];
                modeloDeJugador[2] = pantalla[posicionJugadorX][posicionJugadorY][2];
                modeloDeJugador[3] = pantalla[posicionJugadorX][posicionJugadorY][3];
                modeloDeJugador[4] = pantalla[posicionJugadorX][posicionJugadorY][4];

                imprimirMensaje(1, " ", true);
                imprimirMensaje(1, "Hola! Gran hombre como estas? mi nombre es Alexito y el tuyo?", false);
                imprimirMensaje(2, " ", true);
                imprimirMensaje(2, "Voy a otra region llama Lodric, veo que eres Sutir, tienes prisa?", false);
                imprimirMensaje(3, " ", true);
                imprimirMensaje(3, "Quiero algo a cambio de llevarte a Lodric, traeme cuatro monedas y tenemos un trato.", false);
            }
        }
        else if (pantalla[posicionJugadorX + 1][posicionJugadorY][5] == 'z' || pantalla[posicionJugadorX - 1][posicionJugadorY][5] == 'z' || pantalla[posicionJugadorX][posicionJugadorY + 1][5] == 'z' || pantalla[posicionJugadorX][posicionJugadorY - 1][5] == 'z') {
            actoActual++;
            cambioPantalla(actoActual);
            cambiarEstadoDeCasilla(9, 59, ' ', 179, ' ', 179, 0, 'z');
            posicionJugadorX = 1;
            posicionJugadorY = 1;
        }
        break;

        /*
        case 'v':
            cambioPantalla(1);
            break;
        case 'b':
            cambioPantalla(2);
            break;
        case 'n':
            cambioPantalla(3);
            break;
        */
    }

}

// Empieza la funcion principal
int main()
{
    // Funcion para mostrar al jugador en su casilla correspondiente
    actualizarVisualJugador();

    // Empieza la primera etapa de la historia
    imprimirHistoria(etapaDeHistoria);
    etapaDeHistoria++;

    // Pequena pausa antes de imprimir las instrucciones despues de la historia
    Sleep(1000);
    revisarCasillasCercanas();
    system("cls");
    imprimirPantalla();

    // Ciclo infinito para el mantenimiento del juego
    while (true) {
        imprimirMensajesConstantes();
        // Funcion para capturar el caracter ingresado por el usuario
        entradaUsuario();

        revisarCasillasCercanas();

        // Funcion para mostrar al jugador en su casilla correspondiente
        actualizarVisualJugador();
        // Funcion de windows.h la cual limpia la consola para re-imprimir la pantalla
        system("cls");
        // Se vuelve a desplegar la pantalla
        imprimirPantalla();
    }

    return 0;
}
