void actualizarVisualJugador() {
    // Se asignan las partes del modelo en su respectiva posicion de su casilla
    pantalla[posicionJugadorX][posicionJugadorY][0] = modeloDeJugador[0];
    pantalla[posicionJugadorX][posicionJugadorY][1] = modeloDeJugador[1];
    pantalla[posicionJugadorX][posicionJugadorY][2] = modeloDeJugador[2];
    pantalla[posicionJugadorX][posicionJugadorY][3] = modeloDeJugador[3];
    pantalla[posicionJugadorX][posicionJugadorY][5] = modeloDeJugador[4];
}


// Funcion para cambiar el color del texto.
void colores(unsigned char color) {
    // Se captura el HANDLE de salida de la consola standar usando la funcion GetStdHandle de Windows
    HANDLE handleSalida = GetStdHandle(STD_OUTPUT_HANDLE);

    // Condicional para decidir que color se usara en base al caracter n6 de la casilla
    // Bordes
    if (color == 'a') {
        // Se usa el HANDLE para decirle al programa en que consola aplicar el cambio
        // El numero 6 corresponde al color amarillo
        SetConsoleTextAttribute(handleSalida, 6);
    }
    // ?
    else if (color == 'b') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 11);
    }
    // Camino
    else if (color == 'c') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 7);
    }
    // Pasto
    else if (color == 'd') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 10);
    }
    // Arboles
    else if (color == 'e') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 2);
    }
    // Fantasmas
    else if (color == 'f') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 11);
    }
    // Personaje
    else if (color == 'p') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 14);
    }
}

// Funcion que desplegara la matriz completa (El pantalla)
void imprimirPantalla() {
    // Ciclo que se encarga de imprimir cada elemento de la matriz

    // Se itera en base a las filas de la matriz
    for (int i = 0; i < 20; i++) {
        // Se itera en base a las columnas de la matriz
        for (int j = 0; j < 60; j++) {
            // Se manda a llamar la funcion del color, cambiando el color a el establecido en el elemento con indice 5 de la casilla actual
            colores(pantalla[i][j][5]);
            // Se despliegan las dos primeros elementos de la casilla
            cout << pantalla[i][j][0] << pantalla[i][j][1];
            // Casilla:
            // [][] <- Fila que se imprime
            // [][]
        }
        // Salto de linea para pasar a los siguientes dos elementos de la casilla
        cout << endl;

        // Se repite el proceso con la fila de abajo
        for (int j = 0; j < 60; j++) {
            // Se manda a llamar la funcion del color, cambiando el color a el establecido en el elemento con indice 5 de la casilla actual
            colores(pantalla[i][j][5]);
            // Se imprimen los primeros dos elementos de la casilla
            cout << pantalla[i][j][2] << pantalla[i][j][3];
            // Casilla:
            // [][]
            // [][] <- Fila que se imprime
        }
        cout << endl;
    }
}

// Procedimiento donde se captura lo ingresado por el usuario
void entradaUsuario() {
    char caracterIngresado = _getch();
    // Se manda a llamar la funcion para guardar la casilla
    actualizarEstadoPrevioDeCasilla();

    // Switch que decide lo que se hara en base a inputs de movimiento (w, a, s, d)
    switch (caracterIngresado) {
    case 'w':
        if (pantalla[posicionJugadorX - 1][posicionJugadorY][4] == '1') {
            // Se mueve de posicion al personaje
            posicionJugadorX--;
        }
        break;
    case 'a':
        if (pantalla[posicionJugadorX][posicionJugadorY - 1][4] == '1') {
            posicionJugadorY--;
        }
        break;
    case 's':
        if (pantalla[posicionJugadorX + 1][posicionJugadorY][4] == '1') {
            posicionJugadorX++;
        }
        break;
    case 'd':
        if (pantalla[posicionJugadorX][posicionJugadorY + 1][4] == '1') {
            posicionJugadorY++;
        }
        break;
    case 'i':
        imprimirMensaje(1, " ", true);
        break;
    case 'l':
        imprimirMensaje(2, "Bienvenido a Farlan", false);
        imprimirMensaje(0, "Mensaje test", false);
        imprimirMensaje(1, "Erace una vez", false);
        imprimirMensaje(3, "Diego kano kano kano kano kano kano kano kano kano kano kano kano", false);
        break;
    }
}

void imprimirHistoria(int etapaDeHistoria) {
    switch (etapaDeHistoria) {
    case -1:
        imprimirMensaje(1, " ", true);
        imprimirMensaje(2, " ", true);
        imprimirMensaje(3, " ", true);
        imprimirMensaje(1, conseguirMensaje(15), false);

        system("cls");
        imprimirPantalla();


        break;
    case 0:
        imprimirMensaje(1, conseguirMensaje(0), false);
        imprimirMensaje(2, conseguirMensaje(1), false);
        imprimirMensaje(3, conseguirMensaje(2), false);
        imprimirPantalla();

        Sleep(10000);
        system("cls");

        imprimirMensaje(1, " ", true);
        imprimirMensaje(2, " ", true);
        imprimirMensaje(3, " ", true);
        imprimirMensaje(1, conseguirMensaje(3), false);
        imprimirMensaje(2, conseguirMensaje(4), false);
        imprimirPantalla();

        Sleep(7000);
        system("cls");

        imprimirMensaje(1, " ", true);
        imprimirMensaje(2, " ", true);
        imprimirMensaje(3, " ", true);
        imprimirMensaje(1, conseguirMensaje(5), false);
        imprimirPantalla();

        Sleep(3000);
        system("cls");

        imprimirMensaje(0, " ", true);

        imprimirPantalla();
        break;
    }

}

// Empieza la funcion principal
int main()
{
    int etapaDeHistoria = 0;
    // Funcion para guardar el visual de la casilla antes de reescribirla
    capturarEstadoPrevioDeCasilla();

    // Funcion para mostrar al jugador en su casilla correspondiente
    actualizarVisualJugador();

    // Empieza la primera etapa de la historia
    imprimirHistoria(etapaDeHistoria);
    etapaDeHistoria++;

    // Instrucciones de movimiento
    imprimirHistoria(-1);
    // Ciclo infinito para el mantenimiento del juego
    while (true) {
        // Funcion para capturar el caracter ingresado por el usuario
        entradaUsuario();
        // Funcion para guardar la casilla antes de reescribirla
        capturarEstadoPrevioDeCasilla();
        // Funcion para mostrar al jugador en su casilla correspondiente
        actualizarVisualJugador();
        // Funcion de windows.h la cual limpia la consola para re-imprimir la pantalla
        system("cls");
        // Se vuelve a desplegar la pantalla
        imprimirPantalla();
    }

    return 0;
}
