unsigned char estadoPrevioDeCasilla[4] = {' ',' ',' ',' '}; // Arreglo donde se guarda el estado de la casilla anterior
unsigned char modeloDeJugador[4] = {'@','>','^',' '}; // Arreglo del modelo del personaje
int posicionJugadorX = 1, posicionJugadorY = 1; // Se establece la posicion inicial del personaje

void capturarEstadoPrevioDeCasilla() {
    // Almacena el estado visual actual de la casilla donde está el jugador
    estadoPrevioDeCasilla[0] = pantalla[posicionJugadorX][posicionJugadorY][0];
    estadoPrevioDeCasilla[1] = pantalla[posicionJugadorX][posicionJugadorY][1];
    estadoPrevioDeCasilla[2] = pantalla[posicionJugadorX][posicionJugadorY][2];
    estadoPrevioDeCasilla[3] = pantalla[posicionJugadorX][posicionJugadorY][3];
}

// Se crea la funcion encargada de la actualiza el estado visual
void actualizarEstadoPrevioDeCasilla() {
    // Almacena el estado visual actual de la casilla donde está el jugador para poder restaurarlo después
    pantalla[posicionJugadorX][posicionJugadorY][0] = estadoPrevioDeCasilla[0];
    pantalla[posicionJugadorX][posicionJugadorY][1] = estadoPrevioDeCasilla[1];
    pantalla[posicionJugadorX][posicionJugadorY][2] = estadoPrevioDeCasilla[2];
    pantalla[posicionJugadorX][posicionJugadorY][3] = estadoPrevioDeCasilla[3];
}

// int lineaDeConsola determina en donde se pondra el mensaje
// string mensaje determina el mensaje que se va a desplegar (maximo 116 caracteres)
// bool limpiar determina si se quitara el mensaje actual de la consola
void imprimirMensaje(int lineaDeConsola, string mensaje, bool limpiar)
{
    if(limpiar)
    {
        mensaje = "                                                                                             ";
    }

    int longitudMensaje = mensaje.length();

    if (longitudMensaje % 2 != 0 ) {
        longitudMensaje++;
        mensaje += " ";
    }

    if(lineaDeConsola % 2 == 0)
    {
        for(int i = 0; i < longitudMensaje/2 ; i++)
        {
            pantalla[15+lineaDeConsola/2][i+1][0] = mensaje[i*2];
            pantalla[15+lineaDeConsola/2][i+1][1] = mensaje[(i*2)+1];

        }

    }
    else
    {
        for(int i = 0; i < longitudMensaje/2 ; i++)
        {
            pantalla[15+lineaDeConsola/2][i+1][2] = mensaje[i*2];
            pantalla[15+lineaDeConsola/2][i+1][3] = mensaje[(i*2)+1];

        }
    }

}

// Funcion que dibuja al personaje en donde debe de estar
void actualizarVisualJugador() {
    // Se asignan las partes del modelo en su respectiva posicion de su casilla
    pantalla[posicionJugadorX][posicionJugadorY][0] = modeloDeJugador[0];
    pantalla[posicionJugadorX][posicionJugadorY][1] = modeloDeJugador[1];
    pantalla[posicionJugadorX][posicionJugadorY][2] = modeloDeJugador[2];
    pantalla[posicionJugadorX][posicionJugadorY][3] = modeloDeJugador[3];
}

// Funcion para cambiar el color del texto.
void colores(unsigned char color) {
    // Se captura el HANDLE de salida de la consola standar usando la funcion GetStdHandle de Windows
    HANDLE handleSalida = GetStdHandle(STD_OUTPUT_HANDLE);

    // Condicional para decidir que color se usara en base al caracter n6 de la casilla
    if (color == 'a') {
        // Se usa el HANDLE para decirle al programa en que consola aplicar el cambio
        // El numero 6 corresponde al color amarillo
        SetConsoleTextAttribute(handleSalida, 6);
    }
    else if (color == 'b') {
        // El numero 11 corresponde al color aguamarina
        SetConsoleTextAttribute(handleSalida, 11);
    }
}

// Funcion que desplegara la matriz completa (El pantalla)
void imprimirPantalla() {
    // Ciclo que se encarga de imprimir cada elemento de la matriz

    // Se itera en base a las filas de la matriz
    for (int i = 0; i < 20; i++) {
        // Se itera en base a las columnas de la matriz
        for (int j = 0; j < 60; j++) {
            // Se manda a llamar la funcion del color, cambiando el color a el establecido en el elemento con indice 5 de la casilla actual
            colores(pantalla[i][j][5]);
            // Se despliegan las dos primeros elementos de la casilla
            cout << pantalla[i][j][0] << pantalla[i][j][1];
            // Casilla:
            // [][] <- Fila que se imprime
            // [][]
        }
        // Salto de linea para pasar a los siguientes dos elementos de la casilla
        cout << endl;

        // Se repite el proceso con la fila de abajo
        for (int j = 0; j < 60; j++) {
            // Se manda a llamar la funcion del color, cambiando el color a el establecido en el elemento con indice 5 de la casilla actual
            colores(pantalla[i][j][5]);
            // Se imprimen los primeros dos elementos de la casilla
            cout << pantalla[i][j][2] << pantalla[i][j][3];
            // Casilla:
            // [][]
            // [][] <- Fila que se imprime
        }
        cout << endl;
    }
}

// Procedimiento donde se captura lo ingresado por el usuario
void entradaUsuario() {
    char caracterIngresado = _getch();
    // Se manda a llamar la funcion para guardar la casilla
    actualizarEstadoPrevioDeCasilla();

    // Switch que decide lo que se hara en base a inputs de movimiento (w, a, s, d)
    switch(caracterIngresado) {
    case 'w':
        if (pantalla[posicionJugadorX-1][posicionJugadorY][4] == '1') {
            // Se mueve de posicion al personaje
            posicionJugadorX--;
        }
        break;
    case 'a':
        if (pantalla[posicionJugadorX][posicionJugadorY-1][4] == '1') {
            posicionJugadorY--;
        }
        break;
    case 's':
        if (pantalla[posicionJugadorX+1][posicionJugadorY][4] == '1') {
            posicionJugadorX++;
        }
        break;
    case 'd':
        if (pantalla[posicionJugadorX][posicionJugadorY+1][4] == '1') {
            posicionJugadorY++;
        }
        break;
    case 'i':
        imprimirMensaje(1, " ", true);
        break;
    case 'l':
        imprimirMensaje(1, "Bienvenido a Farlan", false);
        imprimirMensaje(2, "Mensaje test", false);
        imprimirMensaje(3, "Erace una vez", false);
        imprimirMensaje(4, "Diego kano kano kano kano kano kano kano kano kano kano kano kano", false);
        break;
    }


}

// Empieza la funcion principal
int main()
{
    // Funcion para guardar la casilla antes de reescribirla
    capturarEstadoPrevioDeCasilla();

    // Funcion para mostrar al jugador en su casilla correspondiente
    actualizarVisualJugador();

    // Se manda a llamar el despliegue de la pantalla
    imprimirPantalla();

    // Ciclo infinito para el mantenimiento del juego
    while(true) {
        // Funcion para capturar el caracter ingresado por el usuario
        entradaUsuario();
        // Funcion para guardar la casilla antes de reescribirla
        capturarEstadoPrevioDeCasilla();
        // Funcion para mostrar al jugador en su casilla correspondiente
        actualizarVisualJugador();
        // Funcion de windows.h la cual limpia la consola para re-imprimir la pantalla
        system("cls");
        // Se vuelve a desplegar la pantalla
        imprimirPantalla();
    }

    return 0;
}
