// Funcion para cambiar el color del texto.
string colores(unsigned char color) {

    //Los colores se aplican usando secuencias de esc
    //Syntax:
    // \x1b[38;2;[r];[g];[b]m
    // \x1b es el codigo hexadecimal para el comando esc

    // Bordes
    if (color == 'a') {
        return "";
    }
    // ?
    else if (color == 'b') {
        return "";
    }
    // Camino
    else if (color == 'c') {
        return "\x1b[38;2;200;190;190m";
    }
    // Pasto
    else if (color == 'd') {
        return "\x1b[38;2;12;186;41m";
    }
    // Arboles
    else if (color == 'e') {
        return "\x1b[38;2;80;44;30m";
    }
    // Fantasmas
    else if (color == 'f') {
        return "";
    }
    // Piso biblioteca
    else if (color == 'g') {
        return "\x1b[38;2;242;167;124m";
    }
    // Librerias
    else if (color == 'h') {
        return "\x1b[38;2;153;106;78m";
    }
    // Bloqueo?
    else if (color == 'i') {
        return "\x1b[38;2;180;180;180m";
    }
    // Hierbas
    else if (color == 'j') {
        return "\x1b[38;2;9;153;34m";
    }
    // casa 1
    else if (color == 'k') {
        return "\x1b[38;2;0;0;0m";
    }
    // casa 2
    else if (color == 'l') {
        return "\x1b[38;2;0;0;0m";
    }
    // Humo
    else if (color == 'm') {
        return "\x1b[38;2;75;75;75m";
    }
    // Jugador
    else if (color == 'p') {
        return "\x1b[38;2;200;200;200m";
    }
}

